function onScroll() {
	var windows = $(window),
		scrolled = windows.scrollTop();
		winHeight = windows.height() * 0.9;
	$(".kj-animate").each(function() {
		var $this = $(this),
			offsetTop = $this.offset().top;
		if(scrolled + winHeight > offsetTop) {
			$this.addClass($this.data('animate'));
		}
	});
}
$(window).on("scroll", onScroll)
onScroll();